CREATE DATABASE webdoor;

DROP TABLE webdoor.customer;
CREATE TABLE webdoor.customer (CUSTOMER_ID INT(5) auto_increment primary key, CKEY INTEGER, CNIC BIGINT, NAME VARCHAR(30), FATHERNAME VARCHAR(30), USERNAME VARCHAR(30), PASSWORD VARCHAR(30), EMAIL VARCHAR(30), ZONE VARCHAR(30), DOR VARCHAR(30), TELEPHONE VARCHAR(30), MOBILE VARCHAR(30), ADDRESS VARCHAR(100), COMMENTS LONGVARCHAR);







DROP TABLE webdoor.PACKAGETOCUST;
CREATE TABLE webdoor.PACKAGETOCUST (
     PACKTOCUST_ID INT auto_increment primary key, 
     WDPACKAGE_ID INT REFERENCES WDPACKAGE(WDPACKAGE_ID), 
	 CUSTOMER_ID INT REFERENCES CUSTOMER(CUSTOMER_ID)
	 );


DROP TABLE webdoor.WDPACKAGE;
CREATE TABLE webdoor.WDPACKAGE (WDPACKAGE_ID INT(11) auto_increment primary key, PACKAGENAME VARCHAR(30), PACKAGEDETAILS INT(11), CREATED Date);


DROP TABLE webdoor.product;
CREATE TABLE webdoor.product (PRODUCT_ID INT(11) auto_increment primary key, MONTH VARCHAR(30), ISSUEDATE DATE, DUEDATE DATE, PAYMENT INT(11), FINE INT(11), CREATED DATE);


DROP TABLE webdoor.invoicetocust;
CREATE TABLE webdoor.invoicetocust (INTOCUST_ID INT(11) auto_increment primary key, PAIDAMOUNT INT(11), PAIDON DATE, STATUS VARCHAR(20), product_PRODUCT_ID INT(11) REFERENCES product(PRODUCT_ID));



DROP TABLE webdoor.customer_invoicetocust;
CREATE TABLE webdoor.customer_invoicetocust (CUSTOMER_CUSTOMER_ID INT(11), invoices_INTOCUST_ID INT(11));




